"use strict";

import globals from "../utilities/globals";
import util from "../utilities/utilities";
import replaceBlocks from "../ReplaceBlocks/ReplaceBlocks";

const trackSyncStreak = tile => {
  globals.game.syncStreakCount++;

  // If the syncstreakcount is consistent with the total syncCount for this streak then light up bright blue
  if (globals.game.syncStreakCount == globals.game.syncCount) {
    utilities.classChangeDelay(tile, 250, "lit--blue");
    // If the player has reached the end of the streak then add the synclength * 10 to their score
    if (globals.game.syncStreakCount == globals.game.syncLength) {
      globals.game.playerScore += globals.game.syncLength * 10;
      replaceBlocks(10); // and replace 10 OR synclength * 10 worth of blocks when they nail the sync

      // Update those dom elements
      globals.game.syncStreakScore.textContent = String(
        `+${globals.game.syncLength * 10}!`
      );
      util.elementLib.classChange(
        globals.game.syncStreakScore,
        "remove",
        "hidden"
      );
      setTimeout(() => {
        util.elementLib.classChange(
          globals.game.syncStreakScore,
          "add",
          "hidden"
        );
      }, 750);
    }
  }
};

const checkStreak = tile => {
  if (globals.game.currentStreak % 3 === 0) {
    // future upgrade: add powerups
    //powerups.init();
  }
  console.log(tile);
  if (globals.game.currentStreak % 10 === 0) {
    globals.game.playerScore += globals.game.currentStreak;
    util.elementLib.classChangeDelay(
      // throwing error here when tapping a block and running scoring re: nextElementSibling
      // tile has no child when game is running but it does if you comment out the code that removes it
      // is it because that code that removes it runs BEFORE this function???
      tile.firstElementChild.nextElementSibling,
      450,
      "flashScore"
    );
  }
};

const changeTileBackground = tile => {
  var color;
  tile.classList.contains("lit--white") ? (color = "white") : (color = "green");
  util.elementLib.classChangeDelay(tile, 250, `changeColor--${color}`);
};

const updateScoreElements = () => {
  elements.titleScore.textContent = String(`Points: ${score.playerScore}`);
  elements.titleStreak.textContent = String(`Streak: ${score.streak}`);

  elements.gameBoardScores.forEach(function (gameBoardScore) {
    gameBoardScore.textContent = String(score.playerScore);
  });

  elements.gameBoardStreaks.forEach(function (gameBoardStreak) {
    gameBoardStreak.textContent = String(score.streak);
  });
};

const scoring = tile => {
  // Always earn 1 point for each good hit but if has sync, replace 3 blocks instead of just 1
  util.elementLib.classCheck(tile, "lit--green")
    ? replaceBlocks(3)
    : replaceBlocks(1);
  globals.game.playerScore++;

  // See if player is still "in the streak" or has hit the full length of the streak
  trackSyncStreak(tile);

  //
  checkStreak(tile);
  // score.updateScoreElements();
  changeTileBackground(tile);
  // utilities.classChangeDelay(tile.firstElementChild, 450, "flashScore");
};

export default scoring;
