* {
  margin: 0;
  padding: 0;
  -webkit-tap-highlight-color: transparent;
}

html,
body {
  width: 100%;
  height: 100%;
  position: relative;
  background-color: black;
  margin: 0;
  padding: 0;
  overflow: hidden;
  display: flex;
  justify-content: center;
}

/* Wrap Styles */
.wrap--blocks {
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}

/* Block & Tile Styles */
.block {
  position: absolute;
  width: 3.23em;
  height: 3em;
  border: 1px solid white;
  background-color: rgba(50, 125, 250, 0.25);
  box-shadow: inset 0 0 30px rgba(50, 125, 250, 0.25);
  transform: scale(1);
  transition: opacity 0.5s ease-in-out;
}

.tile {
  position: absolute;
  width: 4em;
  height: 4em;
  border-radius: 25%;
  cursor: pointer;
  background: rgba(255, 255, 255, 0.5);
}

/* Animation Styles */
@-webkit-keyframes falling {
  from {
    transform: scale(1);
  }
  to {
    transform: scale(-1.5);
  }
}
.falling {
  -webkit-animation: falling 1s ease-in-out 1;
  animation: falling 1s ease-in-out 1;
}

.tile {
  z-index: 10;
}

.lit--white {
  box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px white, 0 0 70px white, 0 0 80px white, 0 0 100px white, 0 0 150px white;
}

.lit--green {
  box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #5aff18, 0 0 70px #5aff18, 0 0 80px #5aff18, 0 0 100px #5aff18, 0 0 150px #5aff18;
}

.lit--blue {
  box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #228dff, 0 0 70px #228dff, 0 0 80px #228dff, 0 0 100px #228dff, 0 0 150px #228dff;
}

@keyframes flashScore {
  0% {
    opacity: 0;
  }
  50% {
    opacity: 1;
    transform: scale(2);
  }
  100% {
    opacity: 0;
    transform: scale(1);
  }
}
.flashScore {
  -webkit-animation: flashScore 0.75s ease-in-out 1;
  -moz-animation: flashScore 0.75s ease-in-out 1;
  animation: flashScore 0.75s ease-in-out 1;
}

@keyframes changeColor--white {
  from {
    background-color: transparent;
  }
  to {
    background-color: white;
  }
}
@keyframes changeColor--blue {
  from {
    background-color: transparent;
  }
  to {
    background-color: #228dff;
  }
}
@keyframes changeColor--green {
  from {
    background-color: transparent;
  }
  to {
    background-color: #5aff18;
  }
}
.changeColor--white {
  -webkit-animation: changeColor--white 0.2s ease-in-out 1;
  -moz-animation: changeColor--white 0.2s ease-in-out 1;
  animation: changeColor--white 0.2s ease-in-out 1;
}

.changeColor--blue {
  -webkit-animation: changeColor--blue 0.2s ease-in-out 1;
  -moz-animation: changeColor--blue 0.2s ease-in-out 1;
  animation: changeColor--blue 0.2s ease-in-out 1;
}

.changeColor--green {
  -webkit-animation: changeColor--green 0.2s ease-in-out 1;
  -moz-animation: changeColor--green 0.2s ease-in-out 1;
  animation: changeColor--green 0.2s ease-in-out 1;
}

.wrap--timer {
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100%;
  font-size: 3em;
  color: white;
  z-index: 5;
}

.lit--blue-timer {
  text-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #228dff, 0 0 70px #228dff, 0 0 80px #228dff, 0 0 100px #228dff, 0 0 150px #228dff;
}

.hidden {
  display: none;
}

.clear {
  opacity: 0;
}

